(function(){
  angular.module("app")

  .directive("swissSeparator", ["$locale", "$filter", swissSeparator]);

  function swissSeparator($locale, $filter) {
    $locale.NUMBER_FORMATS.GROUP_SEP = "'";

    return {
      restrict: "A",
      require: "ngModel",
      scope: {
        ngModel: "="
      },
      link: function(scope, element, attribute, controller) {
        //model -> view
        controller.$formatters.push(formatter);

        //view -> model
        controller.$parsers.push(parser);

        function formatter(modelValue) {
          //number to string
          return $filter('number')(modelValue);  
        }

        function parser(viewValue) {
          //string to number
          return parseInt(viewValue.replace(/'/g,''));
        }

        scope.$watch(
          function(){
            return scope.ngModel
        },
          function(newValue, oldValue) {
            //Method 1: WORKING: type of model remains number.
            controller.$modelValue = controller.$modelValue.toString() //triggers formatter;

            //Method 2: Works BUT type of model becomes string!
            //scope.ngModel = controller.$modelValue.toString() //triggers formatter;

            //Method 3: WORKING: type of model remains number.
            //controller.$modelValue = $filter('number')(controller.$modelValue);
          }
                    );
      }
    }
  }
})();

