  function MyController($http, $q, $scope) {
    var vm = this;

    vm.apiCall = apiCall;

    function apiCall(id) {
      var url = 'https://jsonplaceholder.typicode.com/posts/' 
      //+ id
      ;

      //return needed; else, $http.get() will not have .then(); bug has to do with promise
      return $http.get(url).then(function(response){
        //OBJ
        if (!response.data.length) {
          if (vm.selectedItem1) {
            if (response.data.id === vm.selectedItem1.id)
              return [];
            else
              return [response.data];
          }

          else 
            return [response.data];
        }

        else {
          //ARRAY
          //removing from array (will not need the else part below)
            /*return _.reduce(response.data, function(array, obj, index){
              if (obj) {
                if ((vm.selectedItem1 && obj.id !== vm.selectedItem1.id) || (vm.selectedItem2 && obj.id !== vm.selectedItem2.id))
                    array.splice(index-1, 1);  
              }

              return array;
            }, response.data);*/


          //adding to array (will need the else part below)
          if (vm.selectedItem1 || vm.selectedItem2) {
            return _.reduce(response.data, function(array, obj, index){
                if ((vm.selectedItem1 && obj.id !== vm.selectedItem1.id) || (vm.selectedItem2 && obj.id !== vm.selectedItem2.id))
                  array.push(obj);  

              return array;
            }, []);
          }
          else {

            if (!response.data.length)
              return [response.data];
            else 
              return response.data;
          }
        }

      })

    }
  }


})();
