		vm.nickName = nickName;  
		vm.messageLog = 'Ready to chat!';

		vm.sendMessage = function() {
		  var match = vm.message.match('^\/nick (.*)');

		  if (angular.isDefined(match) && 
			  angular.isArray(match) && match.length === 2) {
			var oldNick = nickName;
			nickName = match[1];
			vm.message = '';
			vm.messageLog = messageFormatter(new Date(), 
							nickName, 'nickname changed - from ' + 
							oldNick + ' to ' + nickName + '!') + 
							vm.messageLog;
			vm.nickName = nickName;
		  }

		  $log.debug('sending message', vm.message);
		  chatSocket.emit('message', nickName, vm.message);
		  $log.debug('message sent', vm.message);
		  vm.message = '';
		};

		$scope.$on('socket:broadcast', function(event, data) {
			$log.debug('got a message', event.name);
			if (!data.payload) {
			  $log.error('invalid message', 'event', event, 
						 'data', JSON.stringify(data));
			  return;
			} 
			$scope.$apply(function() {
			  vm.messageLog = messageFormatter(
					new Date(), data.source, 
					data.payload) + vm.messageLog;
			});
		  });

	}

	function chatSocket (socketFactory) {
		var socket = socketFactory();
      	socket.forward('broadcast');
      	return socket;
	}

	function messageFormatter(date, nick, message) {
		return date.toLocaleTimeString() + ' - ' + 
           nick + ' - ' + 
           message + '\n';
	}

